import React, { Component } from 'react';
import { Button, Form, Label, Input, FormText, FormGroup } from 'reactstrap';
import * as ServiceUtils from '../../Utils/ServiceUtils';


export default class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Email: "",
            UserName: "",
            Password: "",
            Name: "",
            Surname: "",
            Phone: ""
        };
        // this.handleChange = this.handleChange.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
        this.InsertUser = this.InsertUser.bind(this);

    }

    handleChange = event => {
        debugger;
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();
    }

    validateForm() {
        return this.state.UserName.length > 0 &&
            this.state.Password.length > 0 &&
            this.state.Name.length > 0 &&
            this.state.Surname.length > 0 &&
            this.state.Email.length > 0 &&
            this.state.Phone.length > 0
    };

    InsertUser() {
        debugger;
        let data = {
            "Email": this.state.Email,
            "UserName": this.state.UserName,
            "Password": this.state.Password,
            "Name": this.state.Name,
            "Surname": this.state.Surname,
            "Phone": this.state.Phone
        }
    }

    render() {
        return (
            <div  >
                <div className="Register" style={{ backgroundColor: 'aqua' }}>

                    <form onSubmit={this.handleSubmit} style={{ marginLeft: "750px" }}>
                        <FormGroup  >
                            <Input
                                name="Email"
                                onChange={this.handleChange}
                                value={this.state.Email}
                                placeholder="Email"
                                style={{ width: '250px', height: '30px' }} />
                        </FormGroup>
                        <FormGroup>
                            <Input
                                name="Password"
                                value={this.state.Password}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder="Password" />
                        </FormGroup>
                        <FormGroup>
                            <Input
                                name='UserName'
                                value={this.state.UserName}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder='Username' />
                        </FormGroup>
                        <FormGroup>
                            <Input
                                name='Name'
                                value={this.state.Name}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder='Name' />
                        </FormGroup>
                        <FormGroup>
                            <Input
                                name='Surname'
                                value={this.state.Surname}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder='Surname' />
                        </FormGroup>
                        <FormGroup>
                            <Input
                                name='Phone'
                                value={this.state.Phone}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder='Phone' />
                        </FormGroup>

                        <Button
                            backgroundColor="submit"
                            type="submit"
                            onClick={this.InsertUser}
                        >
                            Submit</Button>

                    </form>

                </div>
            </div>
        )
    }
};
