import React, { Component } from 'react'
import * as  ServisUtils from './../../Utils/ServiceUtils'
import { Container, Button, Form, Input, FormGroup, Col } from 'reactstrap'
import { string } from 'prop-types';

export default class AddProduct extends Component {
    constructor(props) {
        super(props);
        this.state = {
            ProductCode:"",
            ProductName: "",
            UnitInStock: ""
        }
    }

    handleChange = event => {
        this.setState({
            [event.target.name]: event.target.value  //state i güncelliyor
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();
    }

    async InsertProduct() {
        let data = {
            "ProductCode":this.state.ProductCode,
            "ProductName": this.state.ProductName,
            "UnitInStock": this.state.UnitInStock
        }
        let url = 'http://localhost:59982/api/Product/add'
        const incomingResponse = await ServisUtils.postRequest(url, data);
    }

    render() {

        return (

            <Form style={{ backgroundColor: 'aqua', marginTop: '100px' }} onSubmit={this.handleSubmit}>
                <div style={{ backgroundColor: 'aqua' }}>
                    <Col sm={{ size: 'auto' , marginbottom:'100px'}}>
                    <FormGroup>
                            <Col sm={{ size: 10, offset: 5 }}>
                                <Input
                                    name="ProductCode"
                                    type="productCode"
                                    onChange={this.handleChange}
                                    value={this.state.ProductCode}
                                    placeholder="Product Code"
                                    style={{ width: '260px', height: '38px' }} />
                            </Col>
                        </FormGroup>
                        <FormGroup>
                            <Col sm={{ size: 10, offset: 5 }}>
                                <Input
                                    name="ProductName"
                                    type="productName"
                                    onChange={this.handleChange}
                                    value={this.state.ProductName}
                                    placeholder="Product Name"
                                    style={{ width: '260px', height: '38px' }} />
                            </Col>
                        </FormGroup>
                        <FormGroup>
                            <Col sm={{ size: 10, offset: 5 }}>
                                <Input
                                    name="UnitInStock"
                                    type="unitInStock"
                                    onChange={this.handleChange}
                                    value={this.state.UnitInStock}
                                    placeholder="Unit In Stock"
                                    style={{ width: '260px', height: '38px' }} />
                            </Col>
                        </FormGroup>
                        <FormGroup>
                            <Col sm={{ size: 10, offset: 5 }}>
                                <Button
                                    color="success"
                                    backgroundColor="submit"
                                    type="submit"
                                    onClick={this.InsertUser}
                                >
                                    Submit</Button> </Col>
                        </FormGroup>
                    </Col>
                </div>
            </Form>

        )
    }

};
