import React, { Component } from 'react';
import { FormGroup, FormControl } from 'react-bootstrap';
import { Alert, Button, UncontrolledAlert } from 'reactstrap';
// import '../Loginn.css';
import GetList2 from './GetList2';
import * as ServiceUtils from '../Utils/ServiceUtils';
import Register from './Login/register';
import { BrowserRouter as Router, Route, Switch, Redirect, BrowserRouter } from 'react-router-dom'


export default class Login extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: "",
            password: "",
            statu: false,
            visible: false,
            IsSingUp: false
        };

        // this.handleChange=this.handleChange.bind(this);
        this.UserCheck = this.UserCheck.bind(this);  //async yapmak icin bind ettik .  "async UserCheck = () => {" olmuyor 
        this.SingUp = this.SingUp.bind(this);
       
    }

    validateForm() {
        return this.state.email.length > 0 && this.state.password.length > 0;
    }

    handleChange = event => {

        this.setState({
            [event.target.type]: event.target.value
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();

    }

    SingUp() {
        this.setState({ IsSingUp: true })
    }


     UserCheck() {

        debugger;
        let data = {
            "UserName": this.state.email,
            "Password": this.state.password
        }
        debugger;
        const incommingResponse =  ServiceUtils.postRequest('http://localhost:59982/api/User/UserCheck', data)
        debugger;
        this.setState({ statu: incommingResponse, visible: true })
    }

    render() {
        debugger;
        if (this.state.statu === true) {
           // return <GetList2 />
           this.props.history.push('/GetList2');
        }
        if (this.state.IsSingUp === true) {
            //return <Register />
            this.props.history.push('/Register');
        }
        return (
            <div>
            
                <div className="Login" style={{ backgroundColor: 'aqua' }}>
                    <form onSubmit={this.handleSubmit} >
                        <FormGroup controlId="email" bsSize="large" >
                            <FormControl
                                autoFocus type="email"
                                value={this.state.email}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder="Email" />
                        </FormGroup>

                        <FormGroup controlId="password" bsSize="large" >
                            <FormControl
                                type="password"
                                value={this.state.password}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder="Password" />
                        </FormGroup>

                        <Button
                            color="success"
                            disabled={!this.validateForm()}
                            type="submit"
                            onClick={this.UserCheck}
                        >
                            Login
                     </Button>
                        <Route path="/GetList2" component={GetList2} />

                        <Button
                            backgroundColor="submit"
                            type="submit"
                            style={{ marginLeft: "20px" }}
                            onClick={this.SingUp}
                        >
                            Sign Up
                     </Button>
                    </form>
                </div>

                <div>
                    <UncontrolledAlert color="danger" isOpen={this.state.visible} >Kullanıcı adı veya parola hatalı !</UncontrolledAlert>
                </div>
            </div>
        );
    }
};
