import React, { Component } from 'react'
import * as  ServisUtils from './../../Utils/ServiceUtils'
import { Container, Button, Form, Input, FormGroup, Col } from 'reactstrap'
import { string } from 'prop-types';

export default class AddProduct extends Component {
    constructor(props) {
        super(props);
        this.state = {
            ProductName: "",
            UnitInStock: ""
        }
    }

    handleChange = event => {
        this.setState({
            [event.target.name]: event.target.value  //state i güncelliyor
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();
    }

    async InsertProduct() {
        let data = {
            "ProductName": this.state.ProductName,
            "UnitInStock": this.state.UnitInStock
        }
        let url = 'http://localhost:59982/api/Product/add'
        const incomingResponse = await ServisUtils.postRequest(url, data);
    }

    render() {

        return (
            <Container>
                <div style={{ backgroundColor: 'aqua' }}>
                    <form onSubmit={this.handleSubmit}>
                        <Col sm={{ size: 'auto' }}>
                            <Form style={{ backgroundColor: 'aqua', marginTop: '100px' }}>
                                <FormGroup>
                                    <Col sm={{ size: 10, offset: 5 }}>
                                        <Input
                                            name="ProductName"
                                            type="productName"
                                            onChange={this.handleChange}
                                            value={this.state.ProductName}
                                            placeholder="Product Name"
                                            style={{ width: '260px', height: '38px' }} />
                                    </Col>
                                    <Col sm={{ size: 10, offset: 5 }}>
                                        <Input
                                            name="ProductName"
                                            type="productName"
                                            onChange={this.handleChange}
                                            value={this.state.ProductName}
                                            placeholder="Product Name"
                                            style={{ width: '260px', height: '38px' }} />
                                    </Col>
                                    <Col sm={{ size: 10, offset: 5 }}>
                                        <Input
                                            name="ProductName"
                                            type="productName"
                                            onChange={this.handleChange}
                                            value={this.state.ProductName}
                                            placeholder="Product Name"
                                            style={{ width: '260px', height: '38px' }} />
                                    </Col>
                                    <Col sm={{ size: 10, offset: 5 }}>
                                        <Input
                                            name="ProductName"
                                            type="productName"
                                            onChange={this.handleChange}
                                            value={this.state.ProductName}
                                            placeholder="Product Name"
                                            style={{ width: '260px', height: '38px' }} />
                                    </Col>
                                </FormGroup>
                            </Form>
                        </Col>
                    </form>
                </div>
            </Container>
        )
    }

};
