export function postRequest(/* string */ url, data, /* func */ callBack) {
    let config = {
      baseURL: "http://localhost:59982/",
      headers: {
        "Content-Type": "application/json",
        'Accept':'application/json',
        "Access-Control-Allow-Origin": "*"
      }
    };

debugger;
    let incommingResponse=  Axios.post(url,data,config);
    incommingResponse=  incommingResponse.json();
    return incommingResponse;
}
