import React, { Component } from 'react';
import { Button, FormGroup, FormControl, ControlLabel } from 'react-bootstrap';
import { BrowserRouter as Link, Route, Router, Redirect } from 'react-router-dom';
import '../Loginn.css';



export default class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: "",
            password: "",
            statu: false
        };
    }

    validateForm() {
        return this.state.email.length > 0 && this.state.password.length > 0;
    }

    handleChange = event => {

        this.setState({
            [event.target.id]: event.target.value
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();

    }

    UserCheck = () => {

debugger;
        this.state.email === "badgil_1@hotmail.com" && this.state.password === "123" ? this.state.statu = true : console.log("basarısız")

    }

    render() {
        debugger;
        if (this.state.statu === true) {
            return <Router> <Redirect to='src/components/GetList2.jsx' /></Router>
        
        }
        return (
            <div className="Login" style={{backgroundColor:'aqua'}}>
                <form onSubmit={this.handleSubmit}   >
                    <FormGroup controlId="email" bsSize="large" >
                        <ControlLabel>Email</ControlLabel>
                        <FormControl 
                        autoFocus type="email" 
                        value={this.state.email}
                         onChange={this.handleChange}
                          style={{width:'250px',height:'30px'}} 
                          placeholder="Email"/>
                    </FormGroup>
                    <br/> <br/>
                    <FormGroup controlId="password" bsSize="large" >
                        <ControlLabel>Password</ControlLabel>
                        <FormControl type="password" value={this.state.password} onChange={this.handleChange} style={{width:'250px',height:'30px'}} />
                    </FormGroup>
                    <br/> <br/>
                    <Button
                        block bsSize="large"
                        disabled={!this.validateForm()}
                        type="submit"
                        onClick={this.UserCheck}
                        placeholder="Password" >
                        Login
                     </Button>
                </form>
            </div>

        );
    }

};
