import React, { Component } from 'react';
import { FormGroup, FormControl } from 'react-bootstrap';
import { Alert, Button ,UncontrolledAlert} from 'reactstrap';
import { BrowserRouter, Link, Route, Router, Redirect } from 'react-router-dom';
import '../Loginn.css';
import GetList2 from './GetList2';



export default class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: "",
            password: "",
            statu: false,
            visible: false
        };
    }

    validateForm() {
        return this.state.email.length > 0 && this.state.password.length > 0;
    }

    handleChange = event => {

        this.setState({
            [event.target.id]: event.target.value
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();

    }

    UserCheck = () => {

        debugger;
        this.state.email === "badgil_1@hotmail.com" && this.state.password === "123" ? this.setState({ statu: true }) : this.setState({ visible: true })

    }

    render() {
        debugger;
        if (this.state.statu === true) {
            return <GetList2/>
            // return (<BrowserRouter>

            //     <Route path='/src/components/GetList2.jsx' Component={GetList2} />

            // </BrowserRouter>)

        }
        return (
            <div>
                <div className="Login" style={{ backgroundColor: 'aqua' }}>
                    <form    >
                        <FormGroup controlId="email" bsSize="large" >

                            <FormControl
                                autoFocus type="email"
                                value={this.state.email}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder="Email" />
                        </FormGroup>

                        <FormGroup controlId="password" bsSize="large" >

                            <FormControl
                                type="password"
                                value={this.state.password}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder="Password" />
                        </FormGroup>

                        <Button
                            color="success"
                            disabled={!this.validateForm()}
                            type="submit"
                            onClick={this.UserCheck}
                        >
                            Login
                     </Button>
                    </form>
                </div>
                <div>
                    {/* <Alert color="danger" isOpen={this.state.visible}><b>HATAAAAAAAAAAAA</b></Alert> */}
                    <UncontrolledAlert color="danger" isOpen={this.state.visible} >Kullan覺c覺 ad覺 veya parola hatal覺 !</UncontrolledAlert>
                </div>
            </div>


        );
    }

};
