import React, { Component } from 'react';
import { FormGroup, FormControl } from 'react-bootstrap';
import { Alert, Button, UncontrolledAlert } from 'reactstrap';
// import '../Loginn.css';
import GetList2 from './GetList2';
import * as ServiceUtils from '../Utils/ServiceUtils';



export default class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: "",
            password: "",
            statu: false,
            visible: false
        };
        // this.handleChange=this.handleChange.bind(this);
        this.UserCheck = this.UserCheck.bind(this);  //async yapmak icin bind ettik .  "async UserCheck = () => {" olmuyor 
    }

    validateForm() {
        return this.state.email.length > 0 && this.state.password.length > 0;
    }

    handleChange = event => {

        this.setState({
            [event.target.type]: event.target.value
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();

    }

    SingUp(){
        return <GetList2/>
    }

    async UserCheck() {

        debugger;
        let data = {
            "UserName": this.state.email,
            "Password": this.state.password
        }
        debugger;
        const incommingResponse = await ServiceUtils.postRequest('http://localhost:59982/api/User/UserCheck', data)
        debugger;
        this.setState({ statu: incommingResponse, visible: true })
        // this.state.email === "badgil_1@hotmail.com" && this.state.password === "123" ? this.setState({ statu: true }) : this.setState({ visible: true })

    }

    render() {
        debugger;
        if (this.state.statu === true) {
            return <GetList2 />
        }
        return (
            <div>
                <div className="Login" style={{ backgroundColor: 'aqua' }}>
                    <form onSubmit={this.handleSubmit} >
                        <FormGroup controlId="email" bsSize="large" >
                            <FormControl
                                autoFocus type="email"
                                value={this.state.email}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder="Email" />
                        </FormGroup>

                        <FormGroup controlId="password" bsSize="large" >
                            <FormControl
                                type="password"
                                value={this.state.password}
                                onChange={this.handleChange}
                                style={{ width: '250px', height: '30px' }}
                                placeholder="Password" />
                        </FormGroup>

                        <Button
                            color="success"
                            disabled={!this.validateForm()}
                            type="submit"
                            onClick={this.UserCheck}
                        >
                            Login
                     </Button>

                        <Button
                            backgroundColor="submit"
                            type="submit"
                            style={{ marginLeft: "20px" }}
                            onClick={this.SingUp}
                        >
                            Sign Up
                     </Button>

                    </form>
                </div>

                <div>
                    <UncontrolledAlert color="danger" isOpen={this.state.visible} >Kullanıcı adı veya parola hatalı !</UncontrolledAlert>
                </div>
            </div>


        );
    }

};
