import { Switch, Route, HashRouter, Link, Router, BrowserRouter } from 'react-router-dom';
import App from "../App";
import Login from "../components/Login";



export async function getRequest(url) {
    var config = {
        headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Authorization': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjAiLCJlbWFpbCI6ImRlcmluMTJAZ21haWwuY29tIiwicGhvbmUiOiIwNTY5IDg3NCA1OCA1OCIsImV4cCI6MTU1NzA4NDE1M30.6EtNu4RGgpFHou4CoNqAcFwmWrUfCL0LcWU-Z2hBrn8'
        },
    };
    debugger;

    let incomingResponse = await fetch(url, config);
    if (incomingResponse.status === 401) {
        console.log("401");
       // this.props.history.push('/Register');
       <Route path="/" component={Login}/>
    }
    console.log("çıktı");
    //incomingResponse = await incomingResponse.json();
    console.log("json  çevirdi ve return edecek");
    return incomingResponse;


}


export async function postRequest(url, data) {
    var config = {
        headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*'
        },
    };
    debugger;
    let incommingResponse = await Axios.post(url, data, config);
    debugger;
    return incommingResponse.data;
}
export default ()=>(
    <div>
        <Router>
            <Route component={App}/>
        </Router>
    </div>
)
